import React, { Component } from 'react';
import Image from './Image'; // Update the import path for Image
import Title from './Title'; // Update the import path for Title
import './RecipeCard.css'; // Create a RecipeCard.css file for styling

class RecipeCard extends Component {
  render() {
    const meals = this.props.meals;

    return (
      <div className="recipe-card-container">
        {meals.map((meal) => (
          <div key={meal.idMeal} className="recipe-card">
            <Image source={meal.strMealThumb} text={meal.strMeal} />
            <Title title={meal.strMeal} />
          </div>
        ))}
      </div>
    );
  }
}

export default RecipeCard;
