import React, { useState, useEffect } from 'react';
import './App.css';
import { recipes } from './RecipeData';
import RandomRecipe from './components/RandomRecipe';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [randomIndex, setRandomIndex] = useState(null);

  useEffect(() => {
    // Generate a random index only on initial page load
    if (randomIndex === null) {
      const newRandomIndex = Math.floor(Math.random() * recipes.length);
      setRandomIndex(newRandomIndex);
    }
  }, [randomIndex]);

  const handleSearch = () => {
    const searchTerm = searchInput.toLowerCase().trim();

    if (searchTerm === '' || searchTerm.length < 3) {
      // Clear filtered recipes if search term is empty or less than 3 characters
      setFilteredRecipes([]);
    } else {
      const searchIngredients = searchTerm.split(',').map((ingredient) => ingredient.trim());

      if (searchIngredients.length > 0) {
        const matchingRecipes = recipes.filter((recipe) =>
          searchIngredients.every((searchIngredient) =>
            recipe.ingredients.some((ingredient) =>
              ingredient.toLowerCase().includes(searchIngredient)
            )
          )
        );
        setFilteredRecipes(matchingRecipes);
      } else {
        // Clear filtered recipes if no valid search ingredients
        setFilteredRecipes([]);
      }
    }
  };


  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    setSearchInput(inputValue);

    if (inputValue.trim() === '') {
      setFilteredRecipes([]);
      setSelectedRecipe(null);
    } else {
      handleSearch();
    }
  };

  const openRecipeModal = (recipe) => {
    setSelectedRecipe(recipe);
  };

  const closeRecipeModal = () => {
    setSelectedRecipe(null);
  };

  return (
    <div className="App">
      <div className="header">
        {/* ... (unchanged) */}
      </div>
      <div className="content">
        <div className="left-container">
          <div className="Random-Recipe-container">
            <RandomRecipe randomIndex={randomIndex} setRandomIndex={setRandomIndex} />
          </div>
        </div>
        <div className="right-container">
          <div className="search-container">
            {/* ... (unchanged) */}
          </div>
          <div className="recipes-container">
            {filteredRecipes.length > 0 ? (
              filteredRecipes.map((recipe) => (
                <div key={recipe.id} className="recipe" onClick={() => openRecipeModal(recipe)}>
                  <img src={recipe.image} alt={recipe.name} />
                  <p>{recipe.name}</p>
                </div>
              ))
            ) : (
              <p>No matching recipes found.</p>
            )}
          </div>
        </div>
      </div>
      {selectedRecipe && (
        <RecipeModal recipe={selectedRecipe} onClose={closeRecipeModal} />
      )}
    </div>
  );
}

export default App;
