// src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [recipes, setRecipes] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `https://www.thecocktaildb.com/api/json/v1/1/filter.php?i=${searchInput}`
      );

      // Check if the response data and drinks are not null
      if (response.data && response.data.drinks) {
        setRecipes(response.data.drinks);
      } else {
        setRecipes([]); // Set an empty array if no drinks are found
      }
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  return (
    <div className="App">
      <h1>Recipe Search</h1>
      <p>Ingredient-based Filipino recipe search</p>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter ingredients..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="recipes-container">
        {recipes && recipes.length > 0 ? (
          <ul>
            {recipes.map((recipe) => (
              <li key={recipe.idDrink}>
                <img src={recipe.strDrinkThumb} alt={recipe.strDrink} />
                {recipe.strDrink}
              </li>
            ))}
          </ul>
        ) : (
          <p>No matching recipes found.</p>
        )}
      </div>
    </div>
  );
}

export default App;
