// src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [currentRecipeIndex, setCurrentRecipeIndex] = useState(0);

  const API_KEY = 'YOUR_SPOONACULAR_API_KEY'; // Replace with your actual Spoonacular API key

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/complexSearch?cuisine=filipino&query=${searchInput}&apiKey=${API_KEY}`
      );

      // Check if the response data and results are not null
      if (response.data && response.data.results) {
        setRecipes(response.data.results);
        setCurrentRecipeIndex(0); // Reset index when searching
      } else {
        setRecipes([]); // Set an empty array if no recipes are found
      }
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const handleNextRecipe = () => {
    setCurrentRecipeIndex((prevIndex) => (prevIndex + 1) % recipes.length);
  };

  const handlePreviousRecipe = () => {
    setCurrentRecipeIndex((prevIndex) => (prevIndex - 1 + recipes.length) % recipes.length);
  };

  return (
    <div className="App">
      <h1>Recipe Search</h1>
      <p>Ingredient-based Filipino recipe search</p>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter ingredients..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="recipes-container">
        {recipes && recipes.length > 0 ? (
          <div className="recipe">
            <img src={recipes[currentRecipeIndex].image} alt={recipes[currentRecipeIndex].title} />
            <p>{recipes[currentRecipeIndex].title}</p>
          </div>
        ) : (
          <p>No matching recipes found.</p>
        )}
        {recipes.length > 1 && (
          <>
            <button className="nav-button" onClick={handlePreviousRecipe}>
              Previous
            </button>
            <button className="nav-button" onClick={handleNextRecipe}>
              Next
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
