import React, { useState, useEffect } from 'react';
import './App.css';
import { recipes } from './components/RecipeData';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState([]);

  useEffect(() => {
    // Filter recipes based on the search input
    const matchingRecipes = recipes.filter((recipe) =>
      recipe.ingredients.some((ingredient) =>
        ingredient.toLowerCase().includes(searchInput.toLowerCase())
      )
    );
    setFilteredRecipes(matchingRecipes);
  }, [searchInput]);

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    setSearchInput(inputValue);
  };

  return (
    <div className="App">
      <div className="header">
        <div className="header-text">
          <h1>Cuisine Code</h1>
          <p>Ingredient-based Filipino recipe search</p>
        </div>
      </div>
      <div className="content">
        <div className="right-container">
          <div className="search-container">
            <input
              type="text"
              placeholder="Enter ingredients..."
              value={searchInput}
              onChange={handleInputChange}
            />
          </div>
          <div className="recipes-container">
            {filteredRecipes.length > 0 ? (
              filteredRecipes.map((recipe) => (
                <div key={recipe.id} className="recipe">
                  <img src={recipe.image} alt={recipe.name} />
                  <p>{recipe.name}</p>
                </div>
              ))
            ) : (
              // Render all recipes if no search input
              recipes.map((recipe) => (
                <div key={recipe.id} className="recipe">
                  <img src={recipe.image} alt={recipe.name} />
                  <p>{recipe.name}</p>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
