// src/App.js
import React, { useState } from 'react';
import './App.css';
import { recipes } from '../RecipeData';
import RecipeModal from './RecipeModal';


function App() {
  const [searchInput, setSearchInput] = useState('');
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);

  const handleSearch = () => {
    const searchTerm = searchInput.toLowerCase().trim();

    if (searchTerm === '') {
      // Clear the filtered recipes if the search input is empty
      setFilteredRecipes([]);
    } else {
      // Filter recipes based on ingredients
      const matchingRecipes = recipes.filter((recipe) =>
        recipe.ingredients.some((ingredient) => ingredient.toLowerCase().includes(searchTerm))
      );

      setFilteredRecipes(matchingRecipes);
    }
  };

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    setSearchInput(inputValue);

    // Clear the filtered recipes and close the modal if the input is empty
    if (inputValue.trim() === '') {
      setFilteredRecipes([]);
      setSelectedRecipe(null);
    } else {
      handleSearch();
    }
  };

  const openRecipeModal = (recipe) => {
    setSelectedRecipe(recipe);
  };

  const closeRecipeModal = () => {
    setSelectedRecipe(null);
  };

  return (
    <div className="App">
      <h1>Recipe Search</h1>
      <p>Ingredient-based Filipino recipe search</p>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter ingredients..."
          value={searchInput}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="recipes-container">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe) => (
            <div key={recipe.id} className="recipe" onClick={() => openRecipeModal(recipe)}>
              <img src={recipe.image} alt={recipe.name} />
              <p>{recipe.name}</p>
            </div>
          ))
        ) : (
          <p>No matching recipes found.</p>
        )}
      </div>
      {selectedRecipe && (
        <RecipeModal recipe={selectedRecipe} onClose={closeRecipeModal} />
      )}
    </div>
  );
}

export default App;
