// App.js
import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import SearchBar from './components/SearchBar';
import RecipeCard from './components/RecipeCard';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allMeals: [],
      filteredMeals: [],
    };
  }

  componentDidMount() {
    // You need to replace 'YOUR_EDAMAM_APP_ID' and 'YOUR_EDAMAM_APP_KEY' with your actual Edamam API credentials.
    const appId = 'YOUR_EDAMAM_APP_ID';
    const appKey = 'YOUR_EDAMAM_APP_KEY';
    const URL = `https://api.edamam.com/search?app_id=${appId}&app_key=${appKey}&q=chicken`; // You can replace 'chicken' with any default query or remove it.

    axios
      .get(URL)
      .then((res) => {
        const allMeals = res.data.hits || [];
        if (Array.isArray(allMeals)) {
          this.setState({ allMeals, filteredMeals: allMeals });
        } else {
          console.error('Invalid response format');
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  handleSearch = (searchInput) => {
    const filteredMeals = this.state.allMeals.filter((meal) =>
      meal.recipe.ingredientLines.some((ingredient) =>
        ingredient.toLowerCase().includes(searchInput.toLowerCase())
      )
    );
    this.setState({ filteredMeals });
  };

  render() {
    return (
      <div className="App">
        <SearchBar onSearch={this.handleSearch} />
        <div>
          {this.state.filteredMeals.length > 0 ? (
            <RecipeCard meals={this.state.filteredMeals} />
          ) : (
            <p>No matching recipes found.</p>
          )}
        </div>
      </div>
    );
  }
}

export default App;
