// src/App.js
import React, { useState } from 'react';
import './App.css';
import FilipinoRecipes from './data/filipinoRecipes.json';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [currentRecipeIndex, setCurrentRecipeIndex] = useState(0);

  const handleSearch = () => {
    const filteredRecipes = FilipinoRecipes.recipes.filter((recipe) =>
      recipe.ingredients.includes(searchInput.toLowerCase())
    );

    setRecipes(filteredRecipes);
    setCurrentRecipeIndex(0); // Reset index when searching
  };

  const handleNextRecipe = () => {
    setCurrentRecipeIndex((prevIndex) => (prevIndex + 1) % recipes.length);
  };

  return (
    <div className="App">
      <h1>Recipe Search</h1>
      <p>Ingredient-based Filipino recipe search</p>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter ingredients..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="recipes-container">
        {recipes && recipes.length > 0 ? (
          <div className="recipe">
            <img src={recipes[currentRecipeIndex].image} alt={recipes[currentRecipeIndex].name} />
            <p>{recipes[currentRecipeIndex].name}</p>
          </div>
        ) : (
          <p>No matching recipes found.</p>
        )}
        {recipes.length > 1 && (
          <button className="next-button" onClick={handleNextRecipe}>
            Next
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
