// App.js
import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import SearchBar from './components/SearchBar';
import RecipeCard from './components/RecipeCard';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      allMeals: [],
      filteredMeals: [],
    };
  }

  componentDidMount() {
    const URL = 'https://www.themealdb.com/api/json/v1/1/latest.php';
    axios
      .get(URL)
      .then((res) => {
        const allMeals = res.data.meals || [];
        if (Array.isArray(allMeals)) {
          this.setState({ allMeals, filteredMeals: allMeals });
        } else {
          console.error('Invalid response format');
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  handleSearch = (searchInput) => {
    const filteredMeals = this.state.allMeals.filter(
      (meal) =>
        meal.strIngredient.toLowerCase().includes(searchInput.toLowerCase())
    );
    this.setState({ filteredMeals });
  };

  render() {
    return (
      <div className="App">
        <SearchBar onSearch={this.handleSearch} />
        <div>
          {this.state.filteredMeals.length > 0 ? (
            <RecipeCard meals={this.state.filteredMeals} />
          ) : (
            <p>No matching recipes found.</p>
          )}
        </div>
      </div>
    );
  }
}

export default App;
