import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import RecipeCard from './components/RecipeCard';
import IngredientsInput from './components/IngredientsInput';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      meal: {},
    };
  }

  handleSearchRecipes = () => {
    // You can use the numberOfIngredients and ingredientInputs in your search logic.
    // For now, I'll just log them for demonstration purposes.
    console.log('Search Recipes button clicked');
  };

  componentDidMount() {
    // Fetching latest recipes on component mount
    const URL = 'https://www.themealdb.com/api/json/v1/1/latest.php';
    axios
      .get(URL)
      .then((res) => {
        const meal = res.data.meals;
        if (typeof meal === 'object') {
          this.setState({ meal });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    const { meal } = this.state;

    return (
      <div className="App">
        <IngredientsInput />
        <button onClick={this.handleSearchRecipes}>Search Recipes</button>

        {meal.length > 0 && <RecipeCard meals={meal} />}
      </div>
    );
  }
}

export default App;
